import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import logoImage from '../assets/Biglogo.png';

const PageContainer = styled.div`
  width: 100%;
  max-width: 390px;
  margin: 0 auto;
  background-color: #ffffff;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
`;

const Header = styled.div`
  padding: 20px;
  border-bottom: 1px solid #e0e0e0;
`;

const LogoImage = styled.img`
  width: 150px;
  height: auto;
`;

const Content = styled.div`
  flex: 1;
  padding: 20px;
`;

const PostTitle = styled.h1`
  font-size: 20px;
  margin-bottom: 10px;
`;

const PostInfo = styled.div`
  font-size: 12px;
  color: #666;
  margin-bottom: 15px;
`;

const PostBody = styled.div`
  font-size: 14px;
  line-height: 1.6;
  margin-bottom: 20px;
`;

const LikeButton = styled.button`
  background-color: ${props => props.liked ? '#ff4081' : '#e0e0e0'};
  color: white;
  border: none;
  padding: 8px 16px;
  border-radius: 20px;
  cursor: pointer;
  font-size: 14px;
  margin-bottom: 20px;
`;

const CommentSection = styled.div`
  margin-top: 20px;
`;

const CommentForm = styled.form`
  display: flex;
  margin-bottom: 15px;
`;

const CommentInput = styled.input`
  flex: 1;
  padding: 8px;
  font-size: 14px;
  border: 1px solid #ddd;
  border-radius: 20px 0 0 20px;
`;

const CommentSubmit = styled.button`
  padding: 8px 16px;
  background-color: #FFD43B;
  color: #0a124d;
  border: none;
  border-radius: 0 20px 20px 0;
  cursor: pointer;
`;

const Comment = styled.div`
  background-color: #f5f5f5;
  padding: 10px;
  margin-bottom: 10px;
  border-radius: 10px;
  font-size: 13px;
`;

// 가상의 API 호출 함수 (실제 구현 시 이 부분을 실제 API 호출로 대체)
const fetchPostData = async (postId) => {
  return new Promise((resolve) => {
    setTimeout(() => {
      resolve({
        id: postId,
        title: "파리 워킹홀리데이 후기",
        author: "재혁",
        date: "2024. 4. 1. 0:00",
        content: "파리에서의 워킹홀리데이 경험을 공유합니다. 에펠탑 근처에서 일하면서 프랑스 문화를 체험했습니다. 현지 사람들과 교류하며 언어 실력도 크게 향상되었습니다.",
        likes: 10,
        comments: [
          { id: 1, author: "user1", content: "좋은 글이네요! 저도 가보고 싶어요." },
          { id: 2, author: "user2", content: "프랑스 워홀 준비 중인데 많은 도움이 됐습니다." }
        ]
      });
    }, 1000);
  });
};

const MobilePostRead = () => {
  const [postData, setPostData] = useState(null);
  const [liked, setLiked] = useState(false);
  const [newComment, setNewComment] = useState('');

  useEffect(() => {
    const loadPostData = async () => {
      const data = await fetchPostData("somePostId");
      setPostData(data);
      setLiked(false);
    };
    loadPostData();
  }, []);

  const handleLike = () => {
    setLiked(!liked);
    setPostData(prev => ({
      ...prev,
      likes: liked ? prev.likes - 1 : prev.likes + 1
    }));
  };

  const handleCommentSubmit = (e) => {
    e.preventDefault();
    if (newComment.trim()) {
      const newCommentObj = {
        id: Date.now(),
        author: "현재 사용자",
        content: newComment
      };
      setPostData(prev => ({
        ...prev,
        comments: [...prev.comments, newCommentObj]
      }));
      setNewComment('');
    }
  };

  if (!postData) return <PageContainer><Content>로딩 중...</Content></PageContainer>;

  return (
    <PageContainer>
      <Header>
        <LogoImage src={logoImage} alt="Through World" />
      </Header>
      <Content>
        <PostTitle>{postData.title}</PostTitle>
        <PostInfo>작성자: {postData.author} | 작성일: {postData.date}</PostInfo>
        <PostBody>{postData.content}</PostBody>
        <LikeButton onClick={handleLike} liked={liked}>
          ❤️ 좋아요 {postData.likes}
        </LikeButton>
        <CommentSection>
          <h3>댓글</h3>
          <CommentForm onSubmit={handleCommentSubmit}>
            <CommentInput 
              type="text" 
              value={newComment}
              onChange={(e) => setNewComment(e.target.value)}
              placeholder="댓글을 입력하세요"
            />
            <CommentSubmit type="submit">등록</CommentSubmit>
          </CommentForm>
          {postData.comments.map((comment) => (
            <Comment key={comment.id}>
              <strong>{comment.author}: </strong>{comment.content}
            </Comment>
          ))}
        </CommentSection>
      </Content>
    </PageContainer>
  );
}

export default MobilePostRead;
