import React from 'react';
import styled from 'styled-components';
import Header from '../components/Header';
import Sidebar from '../components/Sidebar';

const Container = styled.div`
  display: flex;
  flex-direction: row;
  min-height: 100vh;
  background-color: #fff;
`;
const MainContent = styled.div`
  flex: 1;
  padding: 20px;
`;

const PostTitle = styled.h1`
  font-size: 24px;
  margin-bottom: 10px;
`;

const PostInfo = styled.div`
  font-size: 14px;
  color: #666;
  margin-bottom: 20px;
`;

const PostBody = styled.div`
  font-size: 16px;
  line-height: 1.6;
  margin-bottom: 30px;
`;

const LikeButton = styled.button`
  background-color: ${props => props.liked ? '#ff4081' : '#e0e0e0'};
  color: white;
  border: none;
  padding: 10px 20px;
  border-radius: 5px;
  cursor: pointer;
  font-size: 16px;
  margin-bottom: 20px;
`;

const CommentSection = styled.div`
  margin-top: 30px;
`;

const CommentForm = styled.form`
  display: flex;
  margin-bottom: 20px;
`;

const CommentInput = styled.input`
  flex: 1;
  padding: 10px;
  font-size: 14px;
  border: 1px solid #ddd;
  border-radius: 5px 0 0 5px;
`;

const CommentSubmit = styled.button`
  padding: 10px 20px;
  background-color: #2196f3;
  color: white;
  border: none;
  border-radius: 0 5px 5px 0;
  cursor: pointer;
`;

const Comment = styled.div`
  background-color: #f5f5f5;
  padding: 10px;
  margin-bottom: 10px;
  border-radius: 5px;
`;

// Í∞ÄÏÉÅÏùò API Ìò∏Ï∂ú Ìï®Ïàò (Ïã§Ï†ú Íµ¨ÌòÑ Ïãú Ïù¥ Î∂ÄÎ∂ÑÏùÑ Ïã§Ï†ú API Ìò∏Ï∂úÎ°ú ÎåÄÏ≤¥)
const fetchPostData = async (postId) => {
    // Ïã§Ï†úÎ°úÎäî Ïó¨Í∏∞ÏÑú Î∞±ÏóîÎìú APIÎ•º Ìò∏Ï∂úÌï©ÎãàÎã§
    return new Promise((resolve) => {
      setTimeout(() => {
        resolve({
          id: postId,
          title: "230212 - ÏõêÌîºÏä§",
          author: "Ïû¨ÌòÅ",
          date: "2024. 4. 1. 0:00",
          content: "Ïó¨Í∏∞Ïóê Î≥∏Î¨∏ ÎÇ¥Ïö©Ïù¥ Îì§Ïñ¥Í∞ëÎãàÎã§...",
          likes: 10,
          comments: [
            { id: 1, author: "user1", content: "Ï¢ãÏùÄ Í∏ÄÏù¥ÎÑ§Ïöî!" },
            { id: 2, author: "user2", content: "Ïû¨ÎØ∏ÏûàÍ≤å ÏùΩÏóàÏäµÎãàÎã§." }
          ]
        });
      }, 1000); // 1Ï¥à ÌõÑ Îç∞Ïù¥ÌÑ∞ Î∞òÌôò (ÎÑ§Ìä∏ÏõåÌÅ¨ ÏßÄÏó∞ ÏãúÎÆ¨Î†àÏù¥ÏÖò)
    });
  };



const PostRead = () => {
    const [postData, setPostData] = useState(null);
    const [liked, setLiked] = useState(false);
    const [newComment, setNewComment] = useState('');

    useEffect(() => {
        const loadPostData = async () => {
            const data = await fetchPostData("somePostId"); // Ïã§Ï†úÎ°úÎäî Ïó¨Í∏∞Ïóê ÎèôÏ†Å postIdÎ•º ÎÑ£ÏäµÎãàÎã§
            setPostData(data);
            setLiked(false); // Ï¥àÍ∏∞ ÏÉÅÌÉú ÏÑ§Ï†ï
        };
        loadPostData();
    }, []);

    const handleLike = () => {
        setLiked(!liked);
        setPostData(prev => ({
            ...prev,
            likes: liked ? prev.likes - 1 : prev.likes + 1
        }));
        // Ïó¨Í∏∞ÏÑú Ï¢ãÏïÑÏöî ÏÉÅÌÉúÎ•º Î∞±ÏóîÎìúÏóê ÏóÖÎç∞Ïù¥Ìä∏ÌïòÎäî API Ìò∏Ï∂úÏùÑ Ìï† Ïàò ÏûàÏäµÎãàÎã§
    };

    const handleCommentSubmit = (e) => {
        e.preventDefault();
        if (newComment.trim()) {
            const newCommentObj = {
                id: Date.now(), // ÏûÑÏãú ID
                author: "ÌòÑÏû¨ ÏÇ¨Ïö©Ïûê", // Ïã§Ï†úÎ°úÎäî Î°úÍ∑∏Ïù∏Ìïú ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥Î•º ÏÇ¨Ïö©
                content: newComment
            };
            setPostData(prev => ({
                ...prev,
                comments: [...prev.comments, newCommentObj]
            }));
            setNewComment('');
            // Ïó¨Í∏∞ÏÑú ÏÉà ÎåìÍ∏ÄÏùÑ Î∞±ÏóîÎìúÏóê Ï†ÄÏû•ÌïòÎäî API Ìò∏Ï∂úÏùÑ Ìï† Ïàò ÏûàÏäµÎãàÎã§
        }
    };

    if (!postData) return <div>Î°úÎî© Ï§ë...</div>;

    return (
        <>
            <Header />
            <Container>
                <Sidebar />
                <MainContent>
                    <PostTitle>{postData.title}</PostTitle>
                    <PostInfo>ÏûëÏÑ±Ïûê: {postData.author} | ÏûëÏÑ±Ïùº: {postData.date}</PostInfo>
                    <PostBody>{postData.content}</PostBody>
                    <LikeButton onClick={handleLike} liked={liked}>
                        üëç Ï¢ãÏïÑÏöî {postData.likes}
                    </LikeButton>
                    <CommentSection>
                        <h3>ÎåìÍ∏Ä</h3>
                        <CommentForm onSubmit={handleCommentSubmit}>
                            <CommentInput 
                                type="text" 
                                value={newComment}
                                onChange={(e) => setNewComment(e.target.value)}
                                placeholder="ÎåìÍ∏ÄÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
                            />
                            <CommentSubmit type="submit">Îì±Î°ù</CommentSubmit>
                        </CommentForm>
                        {postData.comments.map((comment) => (
                            <Comment key={comment.id}>
                                <strong>{comment.author}: </strong>{comment.content}
                            </Comment>
                        ))}
                    </CommentSection>
                </MainContent>
            </Container>
        </>
    );
}

export default PostRead;
