import React from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import UpHeader from '../components/Header';
import Sidebar from '../components/Sidebar';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  background-color: #fff;
`;

const Form = styled.div`
  background: white;
  padding: 1rem;
  width: 100%;
  max-width: 600px;
  text-align: left;
  border-bottom: 1px solid #ddd;
`;

const Header = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1rem 0;
`;

const Title = styled.h1`
  font-size: 1.5rem;
  margin: 0;
  flex-grow: 1;
  text-align: center;
`;

const CloseButton = styled.button`
  background: none;
  border: none;
  font-size: 1.5rem;
  cursor: pointer;
  margin-right: 15rem;
`;

const SubmitButton = styled.button`
  background: #e7d355;
  color: white;
  border: none;
  border-radius: 20px;
  padding: 0.5rem 1rem;
  cursor: pointer;
  margin-left: 15rem;
`;

const Label = styled.label`
  font-size: 1rem;
  color: #888;
  margin-top: 1rem;
  display: block;
`;

const Input = styled.input`
  width: 100%;
  padding: 0.5rem;
  margin-top: 0.5rem;
  border: none;
  border-bottom: 1px solid #ddd;
  font-size: 1rem;

  &:focus {
    outline: none;
    border-bottom: 1px solid #000;
  }
`;

const TextArea = styled.textarea`
  width: 100%;
  padding: 0.5rem;
  margin-top: 0.5rem;
  border: none;
  border-bottom: 1px solid #ddd;
  font-size: 1rem;
  height: 200px;
  resize: none;

  &:focus {
    outline: none;
    border-bottom: 1px solid #000;
  }
`;

const Writing = ()=>{
    const navigate = useNavigate();

    const handleCancel = () => {
      navigate('/'); // 메인 페이지로 이동, 필요에 따라 변경
    };
    return(
      <>
      <UpHeader></UpHeader>
      <Sidebar></Sidebar>
        <Container>
        <Header>
          <Title>글 쓰기</Title>
          <SubmitButton>완료</SubmitButton>
        </Header>
        <Form>
          <Label htmlFor="title">제목</Label>
          <Input type="text" id="title" placeholder="제목을 입력하세요" />
          <Label htmlFor="content">내용</Label>
          <TextArea id="content" placeholder="내용을 입력하세요." />
        </Form>
      </Container>      
      </>

    );
}
export default Writing;
