import React from 'react';
import styled from 'styled-components';
import manImage from '../assets/man.jpg';
import peopleImage from '../assets/people.jpg';
import travelImage from '../assets/travel.jpg';

// ... (기존 styled components 코드)

const CircleContent = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
  object-position: center;
`;

const CircleText = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  color: white;
  font-size: 24px;
  font-weight: bold;
  text-align: center;
  text-shadow: 2px 2px 4px rgba(0,0,0,0.5);
  z-index: 1;
`;

const Circle = styled.div`
  position: absolute;
  width: 300px;
  height: 300px;
  border-radius: 50%;
  background-color: ${props => props.color};
  display: flex;
  align-items: center;
  justify-content: center;
  overflow: hidden;
  transition: all 0.3s ease;

  ${props => props.position === 'left' && `
    left: 0;
    top: 50px;
  `}

  ${props => props.position === 'center' && `
    left: 50%;
    top: 0;
    transform: translateX(-50%);
  `}

  ${props => props.position === 'right' && `
    right: 0;
    top: 100px;
  `}

  &:hover {
    filter: brightness(85%);
    transform: ${props => props.position === 'center' 
      ? 'translateX(-50%) scale(1.05)' 
      : 'scale(1.05)'};
    box-shadow: 0 0 15px rgba(255, 255, 255, 0.2);
  }
`;

const App = () => {
  return (
    <FullPageContainer>
      <Header>
        <Title>Through World!</Title>
        <Subtitle>당신의 해외 여정을 함께하겠습니다</Subtitle>
      </Header>
      <CirclesContainer>
        <Circle color="#d8fa41" position="left">
          <CircleContent src={manImage} alt="Left content" />
          <CircleText>해외 취업</CircleText>
        </Circle>
        <Circle color="#fdee42" position="center">
          <CircleContent src={peopleImage} alt="Center content" />
          <CircleText>유학</CircleText>
        </Circle>
        <Circle color="#ffcf5e" position="right">
          <CircleContent src={travelImage} alt="Right content" />
          <CircleText>워킹홀리데이</CircleText>
        </Circle>
      </CirclesContainer>
    </FullPageContainer>
  );
};

export default App;
