import React, { useState } from 'react';
import styled from 'styled-components';
import MobileCard from '../components/MobileCard';

const PageContainer = styled.div`
  width: 100%;
  max-width: 390px;
  margin: 0 auto;
  padding: 20px 10px; // 좌우 패딩을 줄임
  background-color: #f8f9fa;
`;

const Title = styled.h1`
  font-size: 24px;
  margin-bottom: 20px;
  margin-top: 50px;
`;

const FilterContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
  margin-bottom: 20px;
`;

const FilterButton = styled.button`
  padding: 8px 12px;
  border: 1px solid #ddd;
  border-radius: 20px;
  background-color: ${props => props.active ? '#ffd43b' : 'white'};
  color: ${props => props.active ? 'black' : '#333'};
  font-size: 14px;
  cursor: pointer;
`;

const SortContainer = styled.div`
  display: flex;
  gap: 10px;
  margin-bottom: 20px;
`;

const Divider = styled.hr`
  border: none;
  border-top: 1px solid #e0e0e0;
  margin: 15px 0;
`;

const MobilePostList = () => {
  const [activeFilter, setActiveFilter] = useState('전체');
  const [activeSort, setActiveSort] = useState('인기순');

  const filters = ['전체', '캐나다', '호주', '뉴질랜드', '영국', '네덜란드', '독일'];
  const sorts = ['인기순', '최신순'];

  const dummyPosts = [
    { id: 1, title: '캐나다 밴쿠버 여행 후기', likes: 42, author: '여행자123', date: '2023-07-15', content: '밴쿠버의 아름다운 자연과 도시 풍경을 소개합니다.' },
    { id: 2, title: '호주 밴쿠버 여행 후기', likes: 42, author: '여행자33', date: '2023-07-25', content: '호주의 아름다운 자연과 도시 풍경을 소개합니다.' },
  ];

  return (
    <PageContainer>
      <Title>커뮤니티</Title>
      <FilterContainer>
        {filters.map(filter => (
          <FilterButton 
            key={filter} 
            active={activeFilter === filter}
            onClick={() => setActiveFilter(filter)}
          >
            {filter}
          </FilterButton>
        ))}
      </FilterContainer>
      <Divider />
      <SortContainer>
        {sorts.map(sort => (
          <FilterButton 
            key={sort} 
            active={activeSort === sort}
            onClick={() => setActiveSort(sort)}
          >
            {sort}
          </FilterButton>
        ))}
      </SortContainer>
      {dummyPosts.map(post => (
        <MobileCard key={post.id} {...post} />
      ))}
    </PageContainer>
  );
};

export default MobilePostList;
