      return fetchedPosts.filter(post => post.country.toLowerCase() === country.toLowerCase());
    }, [country]);


  useEffect(() => {
    const fetchedPosts = fetchPosts();
    setPosts(fetchedPosts);
  }, [fetchPosts]);

