import React, { useState, useEffect } from "react";
import styled from "styled-components";
import Header from "../components/Header";
import Sidebar from "../components/Sidebar";
import { useParams } from "react-router-dom";
import axios from 'axios';

const Container = styled.div`
  display: flex;
  flex-direction: row;
  min-height: 100vh;
  background-color: #fff;
`;
const MainContent = styled.div`
  flex: 1;
  padding: 20px;
`;

const PostTitle = styled.h1`
  font-size: 24px;
  margin-bottom: 10px;
`;

const PostInfo = styled.div`
  font-size: 14px;
  color: #666;
  margin-bottom: 20px;
`;

const PostBody = styled.div`
  font-size: 16px;
  line-height: 1.6;
  margin-bottom: 30px;
`;

const LikeButton = styled.button`
  background-color: ${(props) => (props.liked ? "#ff4081" : "#e0e0e0")};
  color: white;
  border: none;
  padding: 10px 20px;
  border-radius: 5px;
  cursor: pointer;
  font-size: 16px;
  margin-bottom: 20px;
`;

const CommentSection = styled.div`
  margin-top: 30px;
`;

const CommentForm = styled.form`
  display: flex;
  margin-bottom: 20px;
`;

const CommentInput = styled.input`
  flex: 1;
  padding: 10px;
  font-size: 14px;
  border: 1px solid #ddd;
  border-radius: 5px 0 0 5px;
`;

const CommentSubmit = styled.button`
  padding: 10px 20px;
  background-color: #ffd43b;
  color: #0a124d;
  border: none;
  border-radius: 0 5px 5px 0;
  cursor: pointer;
`;

const Comment = styled.div`
  background-color: #f5f5f5;
  padding: 10px;
  margin-bottom: 10px;
  border-radius: 5px;
`;

const PostRead = () => {
  const { postId } = useParams();
  const [postData, setPostData] = useState(null);
  const [liked, setLiked] = useState(false);
  const [newComment, setNewComment] = useState("");

  useEffect(() => {
    const fetchPostData = async () => {
      try {
        const response = await axios.get(`http://43.200.226.225/intern/community/postdetail/${postId}/`);
        setPostData(response.data);
        setLiked(false); // 초기 좋아요 상태 설정
      } catch (error) {
        console.error('Error fetching post data:', error);
      }
    };

    fetchPostData();
  }, [postId]);

  const handleLike = async () => {
    try {
      const response = await axios.post(`http://43.200.226.225/intern/community/like/${postId}/`);
      setLiked(!liked);
      setPostData((prev) => ({
        ...prev,
        likes: response.data.likes,
      }));
    } catch (error) {
      console.error('Error updating like:', error);
    }
  };

  const handleCommentSubmit = async (e) => {
    e.preventDefault();
    if (newComment.trim()) {
      try {
        const response = await axios.post(`http://43.200.226.225/intern/community/comment/${postId}/`, {
          content: newComment,
        });
        setPostData((prev) => ({
          ...prev,
          comments: [...prev.comments, response.data],
        }));
        setNewComment("");
      } catch (error) {
        console.error('Error submitting comment:', error);
      }
    }
  };

  if (!postData) return <div>로딩 중...</div>;

  return (
    <>
      <Header />
      <Container>
        <Sidebar />
        <MainContent>
          <PostTitle>{postData.title}</PostTitle>
          <PostInfo>
            작성자: {postData.author_id || '익명'} | 작성일: {postData.created_at || 'Unknown date'}
          </PostInfo>
          <PostBody>{postData.content}</PostBody>
          <LikeButton onClick={handleLike} liked={liked}>
            ❤️ 좋아요 {postData.likes}
          </LikeButton>
          <CommentSection>
            <h3>댓글</h3>
            <CommentForm onSubmit={handleCommentSubmit}>
              <CommentInput
                type="text"
                value={newComment}
                onChange={(e) => setNewComment(e.target.value)}
                placeholder="댓글을 입력하세요"
              />
              <CommentSubmit type="submit">등록</CommentSubmit>
            </CommentForm>
            {postData.comments && postData.comments.map((comment) => (
              <Comment key={comment.id}>
                <strong>{comment.author_id || '익명'}: </strong>
                {comment.content}
              </Comment>
            ))}
          </CommentSection>
        </MainContent>
      </Container>
    </>
  );
};

export default PostRead;
