export const livingAbroadInfo = [
    {
        id: 1,
        title: "캐나다 밴쿠버 생활 정보",
        likes: 45,
        author: "밴쿠버거주자",
        date: "2023-11-20",
        content: "밴쿠버는 자연과 도시가 조화를 이루는 아름다운 도시입니다. 생활비는 다소 높은 편이며, 특히 주거 비용이 비쌉니다. 대중교통은 잘 발달되어 있으며, 날씨는 비가 자주 오는 편이므로 우산과 방수 신발이 필수입니다. 다양한 문화와 인종이 어우러져 있어 다문화 경험을 할 수 있습니다. 현지에서의 생활을 위해 영어와 프랑스어가 유용합니다.",
        country: "캐나다",
        comments: [
            { id: 1, author: "유학생", content: "밴쿠버에서 학업 중인데 정말 살기 좋은 도시예요." },
            { id: 2, author: "이민자", content: "생활비가 좀 비싸지만 자연 환경이 좋아서 만족합니다." }
        ]
    },
    {
        id: 2,
        title: "호주 시드니 생활 정보",
        likes: 50,
        author: "시드니주민",
        date: "2023-12-01",
        content: "시드니는 아름다운 해변과 활기찬 도시 생활이 매력적인 곳입니다. 생활비는 높은 편이지만, 다양한 직업 기회가 많습니다. 날씨는 온화하며, 여름에는 매우 더울 수 있습니다. 대중교통은 잘 발달되어 있지만, 차를 소유하면 더 편리합니다. 영어가 주요 언어이며, 친절한 현지인들과 쉽게 어울릴 수 있습니다.",
        country: "호주",
        comments: [
            { id: 1, author: "워홀러", content: "시드니에서 워킹홀리데이로 일했는데, 경험이 정말 좋았어요." },
            { id: 2, author: "이민자", content: "해변과 도시가 가까워서 생활이 즐겁습니다." }
        ]
    },
    {
        id: 3,
        title: "뉴질랜드 오클랜드 생활 정보",
        likes: 48,
        author: "오클랜드주민",
        date: "2023-12-20",
        content: "오클랜드는 아름다운 자연과 평화로운 생활 환경이 매력적인 도시입니다. 생활비는 중간 정도이며, 주거 비용도 비교적 합리적입니다. 대중교통이 잘 발달되어 있고, 자전거 이용도 편리합니다. 영어가 주요 언어이며, 친절한 현지인들과 쉽게 어울릴 수 있습니다. 야외 활동과 스포츠를 즐길 수 있는 기회가 많습니다.",
        country: "뉴질랜드",
        comments: [
            { id: 1, author: "워홀러", content: "오클랜드에서의 워킹홀리데이 경험이 정말 좋았습니다." },
            { id: 2, author: "이민자", content: "자연이 아름답고 생활하기 편리합니다." }
        ]
    },
    {
        id: 4,
        title: "영국 런던 생활 정보",
        likes: 60,
        author: "런던거주자",
        date: "2023-12-15",
        content: "런던은 다양한 문화와 역사를 가진 글로벌 도시입니다. 생활비는 매우 높은 편이며, 특히 주거 비용이 비쌉니다. 대중교통이 잘 발달되어 있지만, 교통비도 비쌉니다. 날씨는 변덕스럽고 비가 자주 오므로 항상 우산을 챙기는 것이 좋습니다. 영어가 주요 언어이며, 다양한 국적의 사람들이 어우러져 있어 다문화적인 환경에서 생활할 수 있습니다.",
        country: "영국",
        comments: [
            { id: 1, author: "유학생", content: "런던에서 유학 중인데, 다양한 문화를 접할 수 있어 좋습니다." },
            { id: 2, author: "이민자", content: "생활비가 비싸지만 기회가 많은 도시입니다." }
        ]
    },
    {
        id: 5,
        title: "네덜란드 암스테르담 생활 정보",
        likes: 53,
        author: "암스테르담거주자",
        date: "2023-12-25",
        content: "암스테르담은 자전거 친화적인 도시로 유명합니다. 생활비는 다소 높은 편이지만, 주거 비용은 상대적으로 합리적입니다. 대중교통 시스템이 잘 발달되어 있고, 자전거를 이용하면 더욱 편리합니다. 네덜란드어를 배우는 것이 좋지만, 영어만으로도 생활이 가능합니다. 문화적인 이벤트와 박물관이 많아 풍부한 문화 생활을 즐길 수 있습니다.",
        country: "네덜란드",
        comments: [
            { id: 1, author: "학생", content: "암스테르담의 대학에서 공부 중인데, 자전거로 이동하기 편리해요." },
            { id: 2, author: "예술가", content: "암스테르담의 예술적 분위기가 정말 좋아요." }
        ]
    },
    {
        id: 6,
        title: "독일 베를린 생활 정보",
        likes: 55,
        author: "베를린거주자",
        date: "2023-12-10",
        content: "베를린은 예술과 역사가 살아있는 도시입니다. 생활비는 다른 유럽 주요 도시들에 비해 비교적 저렴한 편입니다. 대중교통 시스템이 매우 효율적이고, 자전거 도로도 잘 마련되어 있습니다. 독일어를 배우는 것이 생활에 큰 도움이 되지만, 많은 사람들이 영어를 사용합니다. 문화적인 이벤트와 박물관이 많아 풍부한 문화 생활을 즐길 수 있습니다.",
        country: "독일",
        comments: [
            { id: 1, author: "학생", content: "베를린의 대학에서 공부 중인데, 생활비가 합리적이에요." },
            { id: 2, author: "예술가", content: "베를린의 예술적 분위기가 정말 좋아요." }
        ]
    }
];
