import React from 'react';
import { useNavigate,Link } from 'react-router-dom';
import styled from 'styled-components';
import logoImage from '../assets/Biglogo.png'; // Î°úÍ≥† Ïù¥ÎØ∏ÏßÄ Í≤ΩÎ°ú
import bannerImage from '../assets/banner.png'; // Î∞∞ÎÑà Ïù¥ÎØ∏ÏßÄ Í≤ΩÎ°ú
import { dummyPosts } from '../dummyPost';
import { hotPosts } from '../HotPost'; // hotPosts Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞
const PageContainer = styled.div`
  width: 100%;
  max-width: 480px; // Î™®Î∞îÏùº ÌôîÎ©¥ ÌÅ¨Í∏∞Ïóê ÎßûÏ∂§
  min-height: 100vh;
  margin: 0 auto; // Ï§ëÏïô Ï†ïÎ†¨
  background-color: #FFFFFF;
  display: flex;
  flex-direction: column;
  align-items: center;
  overflow-x: hidden; // Í∞ÄÎ°ú Ïä§ÌÅ¨Î°§ Î∞©ÏßÄ
`;

const ScrollableContent = styled.div`
  width: 100%;
  overflow-y: auto;
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 0 16px; // Ï¢åÏö∞ Ìå®Îî©
`;

const Section = styled.div`
  width: 100%;
  margin-bottom: 20px;
`;

// Íµ≠Í∞Ä ÏÑ†ÌÉù Î≤ÑÌäº Ïª®ÌÖåÏù¥ÎÑà
const CountryButtonContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: flex-start;
  gap: 8px;
  width: 100%;
`;

// Íµ≠Í∞Ä ÏÑ†ÌÉù Î≤ÑÌäº
const CountryButton = styled.button`
  padding: 8px 16px;
  border-radius: 20px;
  border: 1px solid #ddd;
  background-color: ${props => props.active ? '#FFD700' : 'white'};
  font-size: 14px;
  white-space: nowrap;
`;

// Í≤åÏãúÍ∏Ä Î¶¨Ïä§Ìä∏ ÏïÑÏù¥ÌÖú
const PostItem = styled.div`
  width: 100%;
  padding: 16px;
  border-bottom: 1px solid #eee;
  &:last-child {
    border-bottom: none;
  }
`;

// ÌïòÎã® ÎÑ§ÎπÑÍ≤åÏù¥ÏÖò
const BottomNav = styled.div`
  position: fixed;
  bottom: 0;
  left: 50%;
  transform: translateX(-50%);
  width: 100%;
  max-width: 480px;
  height: 60px;
  background-color: #ffd9007d;
  display: flex;
  justify-content: space-around;
  align-items: center;
`;


const MobileMain = () => {
  const navigate = useNavigate();

  const getFirstPostTitle = (country) => {
    const post = dummyPosts.find(post => post.country === country);
    return post ? post.title : "";
  };

  const getFirstPostId = (country) => {
    const post = dummyPosts.find(post => post.country === country);
    return post ? post.id : null;
  };

  const handleMoreClick = () => {
    navigate('/mcommunity');
  };

  const handleHotMoreClick = () => {
    navigate('/mhots');
  };

  const handleOpenSideBar = () => {
    navigate('/sidebar');
  };

  const handlePreviewClick = (country) => {
    const postId = getFirstPostId(country);
    if (postId) {
      navigate(`/mpostread/dummy/${postId}`);
    }
  };

  const handleHotPostClick = (postId) => {
    navigate(`/mpostread/hot/${postId}`);
  };

  // Ìï´ Í≤åÏãúÎ¨º Ï¢ãÏïÑÏöî ÏàúÏúºÎ°ú Ï†ïÎ†¨
  const sortedHotPosts = hotPosts.slice().sort((a, b) => b.likes - a.likes).slice(0, 5);

  return (
    <PageContainer>
      <ScrollableContent>
        <LogoImage src={logoImage} alt="Through World" />
        <BannerImage src={bannerImage} alt="Banner" />

        <Section>
          <SectionTitle>
            Íµ≠Í∞ÄÎ≥Ñ Ïª§ÎÆ§ÎãàÌã∞
            <More onClick={handleMoreClick}>more</More>
          </SectionTitle>
          <Card>
            <CountryBoard>
              <BoardName>Ï∫êÎÇòÎã§ Í≤åÏãúÌåê</BoardName>
              <PreviewTitle onClick={() => handlePreviewClick("Ï∫êÎÇòÎã§")}>{getFirstPostTitle("Ï∫êÎÇòÎã§")}</PreviewTitle>
            </CountryBoard>
            <CountryBoard>
              <BoardName>Ìò∏Ï£º Í≤åÏãúÌåê</BoardName>
              <PreviewTitle onClick={() => handlePreviewClick("Ìò∏Ï£º")}>{getFirstPostTitle("Ìò∏Ï£º")}</PreviewTitle>
            </CountryBoard>
            <CountryBoard>
              <BoardName>ÎèÖÏùº Í≤åÏãúÌåê</BoardName>
              <PreviewTitle onClick={() => handlePreviewClick("ÎèÖÏùº")}>{getFirstPostTitle("ÎèÖÏùº")}</PreviewTitle>
            </CountryBoard>
            <CountryBoard>
              <BoardName>ÏòÅÍµ≠ Í≤åÏãúÌåê</BoardName>
              <PreviewTitle onClick={() => handlePreviewClick("ÏòÅÍµ≠")}>{getFirstPostTitle("ÏòÅÍµ≠")}</PreviewTitle>
            </CountryBoard>
          </Card>
        </Section>

        <Section>
          <SectionTitle>
            HOT Í≤åÏãúÎ¨º
            <More onClick={handleHotMoreClick}>more</More>
          </SectionTitle>
          <Card>
            {sortedHotPosts.map(post => (
              <CardItem key={post.id} onClick={() => handleHotPostClick(post.id)}>
                {post.title}
                <span><HeartIcon>‚ô•</HeartIcon>{post.likes}</span>
              </CardItem>
            ))}
          </Card>
        </Section>
      </ScrollableContent>

      <BottomNav>
            <NavItem onClick={handleOpenSideBar}>‚ò∞</NavItem>
            <NavItem>
              <Link to={`/mmain`} style={{ color: '#333', textDecoration: 'none' }}>‚åÇ</Link>
            </NavItem>
            <NavItem><Link to={`/mmypage`} style={{ color: '#333', textDecoration: 'none' }}>üë§</Link></NavItem>
      </BottomNav>
    </PageContainer>
  );
};

export default MobileMain;
