import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import Header from '../components/Header';
import Sidebar from '../components/Sidebar';
import PostCard from '../components/PostCard';

const PageLayout = styled.div`
  display: flex;
  min-height: 100vh;
`;

const MainContent = styled.main`
  flex-grow: 1;
  padding: 20px;
  background-color: #f5f5f5;
`;

const PostGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
  gap: 20px;
`;

const Country = styled.div`
    color: #333;
    margin: 0;
    font-size: 24px;
    font-weight: bold;
`

const PostList = () => {
    const [posts, setPosts] = useState([]);

    useEffect(() => {
        const fetchedPosts = [
            {
                id: 1,
                title: "파리에서의 3일",
                likes: 42,
                author: "여행자123",
                date: "2023-07-15",
                preview: "에펠탑부터 루브르 박물관까지, 파리의 주요 명소를 둘러본 여행기입니다.",
                continent: "유럽",
                country: "프랑스"
            },
            {
                id: 2,
                title: "베르사유 궁전 탐방",
                likes: 38,
                author: "역사탐험가",
                date: "2023-07-20",
                preview: "프랑스의 역사가 살아숨쉬는 베르사유 궁전을 탐방했습니다.",
                continent: "유럽",
                country: "프랑스"
            },
            {
                id: 3,
                title: "몽마르뜨 언덕 탐방",
                likes: 55,
                author: "역사탐험가",
                date: "2023-07-20",
                preview: "프랑스의 역사가 살아숨쉬는 베르사유 궁전을 탐방했습니다.",
                continent: "유럽",
                country: "프랑스"
            }
        ];

        // 좋아요 수에 따라 내림차순 정렬
        const sortedPosts = fetchedPosts.sort((a, b) => b.likes - a.likes);
        setPosts(sortedPosts);
    }, []);

    return (
        <>
            <Header />
            <PageLayout>
                <Sidebar />
                <MainContent>
                    <Country>프랑스🇫🇷</Country>
                    <PostGrid>
                        {posts.map(post => (
                            <PostCard 
                                key={post.id}
                                title={post.title}
                                likes={post.likes}
                                author={post.author}
                                date={post.date}
                                preview={post.preview}
                                continent={post.continent}
                                country={post.country}
                            />
                        ))}
                    </PostGrid>
                </MainContent>
            </PageLayout>
        </>
    );
}

export default PostList;
