import React, { useState , useEffect, useCallback } from 'react';
import styled from 'styled-components';
import MobileCard from '../components/MobileCard';
import { dummyPosts } from '../dummyPost';

const PageContainer = styled.div`
  width: 100%;
  max-width: 390px;
  margin: 0 auto;
  background-color: #FFFFFF;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  position: relative;
`;

const ScrollableContent = styled.div`
  flex: 1;
  overflow-y: auto;
  padding: 20px 10px;
`;

const Title = styled.h1`
  font-size: 24px;
  margin-bottom: 20px;
`;

const FilterContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
  margin-bottom: 20px;
`;

const FilterButton = styled.button`
  padding: 8px 12px;
  border: 1px solid #ddd;
  border-radius: 20px;
  background-color: ${props => props.active ? '#ffd43b' : 'white'};
  color: ${props => props.active ? 'black' : '#333'};
  font-size: 14px;
  cursor: pointer;
`;
const Divider = styled.hr`
  border: none;
  border-top: 1px solid #e0e0e0;
  margin: 15px 0;
`;

const BottomNav = styled.div`
  position: fixed;
  bottom: 0;
  left: 50%;
  transform: translateX(-50%);
  width: 100%;
  max-width: 390px;
  height: 60px;
  background-color: #ffec7e;
  display: flex;
  justify-content: space-around;
  align-items: center;
  z-index: 1000;
`;

const NavItem = styled.div`
  font-size: 24px;
`;

const MobilePostList = () => {
    const [posts, setPosts] = useState([]);
    const [activeFilter, setActiveFilter] = useState('전체');
    const [activeSort, setActiveSort] = useState('인기순');

    const countryInfo = {
      전체: { flag: '🌍' },
      캐나다: { flag: '🇨🇦' },
      호주: { flag: '🇦🇺' },
      뉴질랜드: { flag: '🇳🇿' },
      영국: { flag: '🇬🇧' },
      네덜란드: { flag: '🇳🇱' },
      독일: { flag: '🇩🇪' },
    };

    useEffect(() => {
        setPosts(dummyPosts);
      }, []);

    const sortPosts = useCallback((postsToSort) => {
      return [...postsToSort].sort((a, b) => {
        if (activeSort === '인기순') {
          return b.likes - a.likes;
        } else {
          return new Date(b.date) - new Date(a.date);
        }
      });
    }, [activeSort]);

    const filteredAndSortedPosts = useCallback(() => {
      let filtered = activeFilter === '전체' ? posts : posts.filter(post => post.country === activeFilter);
      return sortPosts(filtered);
    }, [posts, activeFilter, sortPosts]);

    return (
        <PageContainer>
          <ScrollableContent>
            <Title>커뮤니티</Title>
            <FilterContainer>
              {Object.entries(countryInfo).map(([country, info]) => (
                <FilterButton 
                  key={country} 
                  active={activeFilter === country}
                  onClick={() => setActiveFilter(country)}
                >
                  {info.flag} {country}
                </FilterButton>
              ))}
            </FilterContainer>
            <Divider />
            <FilterContainer>
              {['인기순', '최신순'].map(sort => (
                <FilterButton 
                  key={sort} 
                  active={activeSort === sort}
                  onClick={() => setActiveSort(sort)}
                >
                  {sort}
                </FilterButton>
              ))}
            </FilterContainer>
            {filteredAndSortedPosts().map(post => (
              <MobileCard key={post.id} {...post} />
            ))}
          </ScrollableContent>
          <BottomNav>
            <NavItem>☰</NavItem>
            <NavItem>⌂</NavItem>
            <NavItem>👤</NavItem>
          </BottomNav>
        </PageContainer>
      );
  };

  export default MobilePostList;
