import React from 'react';
import styled from 'styled-components';

const FullPageContainer = styled.div`
  min-height: 100vh;
  width: 100%;
  background-color: #1a1a1a;
  color: white;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
  box-sizing: border-box;
`;

const Header = styled.header`
  text-align: center;
  margin-bottom: 40px;
`;

const Title = styled.h1`
  font-size: 2.5em;
  margin-bottom: 10px;
`;

const Subtitle = styled.p`
  font-size: 1.2em;
`;

const CirclesContainer = styled.div`
  position: relative;
  width: 100%;
  max-width: 1000px;
  height: 500px;
`;

const Circle = styled.div`
  position: absolute;
  width: 300px;
  height: 300px;
  border-radius: 50%;
  background-color: ${props => props.color};
  display: flex;
  align-items: center;
  justify-content: center;
  overflow: hidden;
  transition: all 0.3s ease; // 부드러운 전환 효과

  ${props => props.position === 'left' && `
    left: 0;
    top: 50px;
  `}

  ${props => props.position === 'center' && `
    left: 50%;
    top: 0;
    transform: translateX(-50%);
  `}

  ${props => props.position === 'right' && `
    right: 0;
    top: 100px;
  `}

  &:hover {
    filter: brightness(85%); // 호버 시 색상을 15% 어둡게
    transform: ${props => props.position === 'center' 
      ? 'translateX(-50%) scale(1.05)' 
      : 'scale(1.05)'}; // 약간 확대 효과
    box-shadow: 0 0 15px rgba(255, 255, 255, 0.2); // 호버 시 약간의 그림자 효과
  }
`;

const CircleContent = styled.img`
  width: 100%;
  height: auto;
`;

const App = () => {
  return (
    <FullPageContainer>
      <Header>
        <Title>Through World!</Title>
        <Subtitle>당신의 해외 여정을 함께하겠습니다</Subtitle>
      </Header>
      <CirclesContainer>
        <Circle color="#d8fa41" position="left">
          <CircleContent alt="Left content" />
        </Circle>
        <Circle color="#fdee42" position="center">
          <CircleContent alt="Center content" />
        </Circle>
        <Circle color="#ffcf5e" position="right">
          <CircleContent alt="Right content" />
        </Circle>
      </CirclesContainer>
    </FullPageContainer>
  );
};

export default App;
