import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import { Link,useNavigate } from 'react-router-dom';
import MobileCard from '../components/MobileCard';
import { hotPosts } from '../HotPost';

const PageContainer = styled.div`
  width: 100%;
  max-width: 390px;
  margin: 0 auto;
  background-color: #FFFFFF;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  position: relative;
`;

const ScrollableContent = styled.div`
  flex: 1;
  overflow-y: auto;
  padding: 20px 10px;
`;

const Title = styled.h1`
  font-size: 24px;
  margin-bottom: 20px;
`;

const BottomNav = styled.div`
  position: fixed;
  bottom: 0;
  left: 50%;
  transform: translateX(-50%);
  width: 100%;
  max-width: 390px;
  height: 60px;
  background-color: #ffec7e;
  display: flex;
  justify-content: space-around;
  align-items: center;
  z-index: 1000;
`;

const NavItem = styled.div`
  font-size: 24px;
`;

const MobileHotList = () => {
  const [posts, setPosts] = useState([]);
  const navigate = useNavigate();
  const handleOpenSideBar = () => {
    navigate('/sidebar');
  };

  useEffect(() => {
    // ì¢‹ì•„ìš” ìˆœìœ¼ë¡œ ì •ë ¬ëœ ê²Œì‹œë¬¼ ì„¤ì •
    const sortedHotPosts = hotPosts.slice().sort((a, b) => b.likes - a.likes);
    setPosts(sortedHotPosts);
  }, []);

  return (
    <PageContainer>
      <ScrollableContent>
        <Title>HOT ê²Œì‹œë¬¼</Title>
        {posts.map(post => (
          <Link to={`/mpostread/hot/${post.id}`} key={post.id} style={{ textDecoration: 'none', color: 'inherit' }}>
            <MobileCard {...post} />
          </Link>
        ))}
      </ScrollableContent>
      <BottomNav>
      <NavItem onClick={handleOpenSideBar}>â˜°</NavItem>
            <NavItem>
              <Link to={`/mmain`}style={{ color: '#333', textDecoration: 'none' }}>âŒ‚</Link>
            </NavItem>
            <NavItem><Link to={`/mmypage`} style={{ color: '#333', textDecoration: 'none' }}>ðŸ‘¤</Link></NavItem>
      </BottomNav>
    </PageContainer>
  );
};

export default MobileHotList;
