//PostList.jsx
import React, { useState, useEffect, useCallback } from "react";
import styled from "styled-components";
import Header from "../components/Header";
import Sidebar from "../components/Sidebar";
import PostCard from "../components/PostCard";
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom";

const PageLayout = styled.div`
  display: flex;
  min-height: 100vh;
`;

const MainContent = styled.main`
  flex-grow: 1;
  padding: 20px;
  background-color: #f5f5f5;
`;

const PostGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
  gap: 20px;
`;
const StyledLink = styled(Link)`
  text-decoration: none;
  /* ÏõêÌïòÎäî Îã§Î•∏ Ïä§ÌÉÄÏùºÎèÑ Ï∂îÍ∞ÄÌï† Ïàò ÏûàÏäµÎãàÎã§ */
  color: inherit; /* ÎßÅÌÅ¨ ÏÉâÏÉÅÏùÑ Î∂ÄÎ™® ÏöîÏÜåÏùò ÏÉâÏÉÅÏúºÎ°ú ÏÉÅÏÜç */
`;

const Country = styled.div`
  color: #333;
  margin: 0;
  font-size: 24px;
  font-weight: bold;
`;

const FilterButtons = styled.div`
  margin-bottom: 20px;
`;

const FilterButton = styled.button`
  margin-right: 10px;
  padding: 5px 10px;
  background-color: ${(props) => (props.active ? "#FFD43B" : "#f8f9fa")};
  color: ${(props) => (props.active ? "white" : "black")};
  border: 1px solid #ffd43b;
  border-radius: 5px;
  cursor: pointer;

  &:hover {
    background-color: #ffd43b;
    color: white;
  }
`;

const PostList = () => {
  const { country } = useParams();
  const [posts, setPosts] = useState([]);
  const [sortBy, setSortBy] = useState("likes"); // 'likes' or 'date'
  const countryInfo = {
    canada: { korName: "Ï∫êÎÇòÎã§", flag: "üá®üá¶" },
    australia: { korName: "Ìò∏Ï£º", flag: "üá¶üá∫" },
    newzealand: { korName: "Îâ¥ÏßàÎûúÎìú", flag: "üá≥üáø" },
    england: { korName: "ÏòÅÍµ≠", flag: "üá¨üáß" },
    netherlands: { korName: "ÎÑ§ÎçúÎûÄÎìú", flag: "üá≥üá±" },
    germany: { korName: "ÎèÖÏùº", flag: "üá©üá™" },
  };
  const fetchPosts = useCallback(() => {
    // Ïó¨Í∏∞ÏÑú DBÎÇò APIÎ°úÎ∂ÄÌÑ∞ Îç∞Ïù¥ÌÑ∞Î•º Í∞ÄÏ†∏ÏòµÎãàÎã§.
    const fetchedPosts = [
      {
        id: 1,
        title: "ÌååÎ¶¨ÏóêÏÑúÏùò 3Ïùº",
        likes: 42,
        author: "Ïó¨ÌñâÏûê123",
        date: "2023-07-15",
        preview:
          "ÏóêÌé†ÌÉëÎ∂ÄÌÑ∞ Î£®Î∏åÎ•¥ Î∞ïÎ¨ºÍ¥ÄÍπåÏßÄ, ÌååÎ¶¨Ïùò Ï£ºÏöî Î™ÖÏÜåÎ•º ÎëòÎü¨Î≥∏ Ïó¨ÌñâÍ∏∞ÏûÖÎãàÎã§.",
        continent: "Ïú†ÎüΩ",
        country: "france",
      },
      {
        id: 2,
        title: "Î≤†Î•¥ÏÇ¨Ïú† Í∂ÅÏ†Ñ ÌÉêÎ∞©",
        likes: 38,
        author: "Ïó≠ÏÇ¨ÌÉêÌóòÍ∞Ä",
        date: "2023-07-20",
        preview: "ÌîÑÎûëÏä§Ïùò Ïó≠ÏÇ¨Í∞Ä ÏÇ¥ÏïÑÏà®Ïâ¨Îäî Î≤†Î•¥ÏÇ¨Ïú† Í∂ÅÏ†ÑÏùÑ ÌÉêÎ∞©ÌñàÏäµÎãàÎã§.",
        continent: "Ïú†ÎüΩ",
        country: "france",
      },
      {
        id: 3,
        title: "ÎÇòÏù¥ÏïÑÍ∞ÄÎùº Ìè≠Ìè¨ Ìà¨Ïñ¥",
        likes: 45,
        author: "Î™ÖÏòàÏ∫êÎÇòÎã§Ïù∏",
        date: "2023-06-30",
        preview: "ÎÇòÏù¥ÏïÑÍ∞ÄÎùº Ìè≠Ìè¨ÏóêÏÑú Î¨¥ÏßÄÍ∞úÎ•º Î≥¥Îã§!",
        continent: "Î∂ÅÎØ∏",
        country: "canada",
      },
      {
        id: 4,
        title: "12ÏÇ¨ÎèÑ Ìà¨Ïñ¥ ÌõÑÍ∏∞",
        likes: 65,
        author: "ÎåÄÌïúÌò∏Ï£ºÏù∏",
        date: "2022-12-23",
        preview: "12ÏÇ¨ÎèÑÏôÄ Í∑∏Î†àÏù¥Ìä∏ Ïò§ÏÖòÎ°úÎìúÎ•º Í∞îÏäµÎãàÎã§.",
        continent: "Ïò§ÏÑ∏ÏïÑÎãàÏïÑ",
        country: "australia",
      },
      {
        id: 5,
        title: "Îâ¥ÏßàÎûúÎìú ÎÇ®ÏÑ¨ Ïó¨Ìñâ",
        likes: 33,
        author: "ÌÇ§ÏúÑÏÉàÎäî ÏßπÏßπ",
        date: "2023-11-30",
        preview: "ÏπúÍµ¨Îûë Í∞ôÏù¥ ÎÇ®ÏÑ¨Ìà¨Ïñ¥Î•º Ïã†Ï≤≠ÌñàÏñ¥Ïöî.",
        continent: "Ïò§ÏÑ∏ÏïÑÎãàÏïÑ",
        country: "newzealand",
      },
      // ‚Ä¶ Îçî ÎßéÏùÄ Ìè¨Ïä§Ìä∏ Îç∞Ïù¥ÌÑ∞
    ];


    const filteredPosts = fetchedPosts.filter(
      (post) => post.country === country
    );
    return filteredPosts;
  }, [country]);

  useEffect(() => {
    const fetchedPosts = fetchPosts();
    setPosts(fetchedPosts);
  }, [fetchPosts]);

  const CountryDisplay = () => {
    const info = countryInfo[country] || { korName: country, flag: "üè≥Ô∏è" };
    return (
      <Country>
        {info.korName} {info.flag}
      </Country>
    );
  };

  const sortPosts = useCallback(
    (postsToSort) => {
      return [...postsToSort].sort((a, b) => {
        if (sortBy === "likes") {
          return b.likes - a.likes;
        } else {
          return new Date(b.date) - new Date(a.date);
        }
      });
    },
    [sortBy]
  );

  useEffect(() => {
    setPosts((prevPosts) => sortPosts(prevPosts));
  }, [sortBy, sortPosts]);

  useEffect(() => {
    const getPosts = async () => {
      const fetchedPosts = await fetchPosts();
      setPosts(fetchedPosts);
    };
    getPosts();
  }, [fetchPosts]);

  const handleSortChange = (newSortBy) => {
    setSortBy(newSortBy);
  };

  return (
    <>
      <Header />
      <PageLayout>
        <Sidebar />
        <MainContent>
          <CountryDisplay />
          <FilterButtons>
            <FilterButton
              onClick={() => handleSortChange("likes")}
              active={sortBy === "likes"}
            >
              Ï¢ãÏïÑÏöî Ïàú
            </FilterButton>
            <FilterButton
              onClick={() => handleSortChange("date")}
              active={sortBy === "date"}
            >
              ÏµúÏã†Ïàú
            </FilterButton>
          </FilterButtons>
          <PostGrid>
            {posts.map((post) => (
              <StyledLink key={post.id} to={`/postread/${post.id}`}>
                <PostCard
                  key={post.id}
                  title={post.title}
                  likes={post.likes}
                  author={post.author}
                  date={post.date}
                  preview={post.preview}
                  continent={post.continent}
                  country={post.country}
                />
              </StyledLink>
            ))}
          </PostGrid>
        </MainContent>
      </PageLayout>
    </>
  );
};

export default PostList;
