import React, { useState , useEffect, useCallback } from 'react';
import styled from 'styled-components';
import MobileCard from '../components/MobileCard';
import {Link,useNavigate} from 'react-router-dom';
import { dummyPosts } from '../dummyPost';

const PageContainer = styled.div`
  width: 100%;
  max-width: 390px;
  margin: 0 auto;
  background-color: #FFFFFF;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  position: relative;
`;

const ScrollableContent = styled.div`
  flex: 1;
  overflow-y: auto;
  padding: 20px 10px;
`;

const Title = styled.h1`
  font-size: 24px;
  margin-bottom: 20px;
`;

const FilterContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
  margin-bottom: 20px;
`;

const FilterButton = styled.button`
  padding: 8px 12px;
  border: 1px solid #ddd;
  border-radius: 20px;
  background-color: ${props => props.active ? '#ffd43b' : 'white'};
  color: ${props => props.active ? 'black' : '#333'};
  font-size: 14px;
  cursor: pointer;
`;
const Divider = styled.hr`
  border: none;
  border-top: 1px solid #e0e0e0;
  margin: 15px 0;
`;

const BottomNav = styled.div`
  position: fixed;
  bottom: 0;
  left: 50%;
  transform: translateX(-50%);
  width: 100%;
  max-width: 390px;
  height: 60px;
  background-color: #ffec7e;
  display: flex;
  justify-content: space-around;
  align-items: center;
  z-index: 1000;
`;

const NavItem = styled.div`
  font-size: 24px;
`;

const MobilePostList = () => {
    const [posts, setPosts] = useState([]);
    const [activeFilter, setActiveFilter] = useState('ì „ì²´');
    const [activeSort, setActiveSort] = useState('ì¸ê¸°ìˆœ');

    const countryInfo = {
      ì „ì²´: { flag: 'ðŸŒ' },
      ìºë‚˜ë‹¤: { flag: 'ðŸ‡¨ðŸ‡¦' },
      í˜¸ì£¼: { flag: 'ðŸ‡¦ðŸ‡º' },
      ë‰´ì§ˆëžœë“œ: { flag: 'ðŸ‡³ðŸ‡¿' },
      ì˜êµ­: { flag: 'ðŸ‡¬ðŸ‡§' },
      ë„¤ëœëž€ë“œ: { flag: 'ðŸ‡³ðŸ‡±' },
      ë…ì¼: { flag: 'ðŸ‡©ðŸ‡ª' },
    };

    useEffect(() => {
        setPosts(dummyPosts);
      }, []);
      const navigate = useNavigate();
      const handleOpenSideBar = () => {
        navigate('/sidebar');
      };

    const sortPosts = useCallback((postsToSort) => {
      return [...postsToSort].sort((a, b) => {
        if (activeSort === 'ì¸ê¸°ìˆœ') {
          return b.likes - a.likes;
        } else {
          return new Date(b.date) - new Date(a.date);
        }
      });
    }, [activeSort]);

    const filteredAndSortedPosts = useCallback(() => {
      let filtered = activeFilter === 'ì „ì²´' ? posts : posts.filter(post => post.country === activeFilter);
      return sortPosts(filtered);
    }, [posts, activeFilter, sortPosts]);

    return (
        <PageContainer>
          <ScrollableContent>
            <Title>ì»¤ë®¤ë‹ˆí‹°</Title>
            <FilterContainer>
              {Object.entries(countryInfo).map(([country, info]) => (
                <FilterButton 
                  key={country} 
                  active={activeFilter === country}
                  onClick={() => setActiveFilter(country)}
                >
                  {info.flag} {country}
                </FilterButton>
              ))}
            </FilterContainer>
            <Divider />
            <FilterContainer>
              {['ì¸ê¸°ìˆœ', 'ìµœì‹ ìˆœ'].map(sort => (
                <FilterButton 
                  key={sort} 
                  active={activeSort === sort}
                  onClick={() => setActiveSort(sort)}
                >
                  {sort}
                </FilterButton>
              ))}
            </FilterContainer>
            {filteredAndSortedPosts().map(post => (
            <Link to={`/mpostread/dummy/${post.id}`} key={post.id} style={{ textDecoration: 'none', color: 'inherit' }}>
              <MobileCard {...post} />
            </Link>
          ))}
          </ScrollableContent>
          <BottomNav>
          <NavItem onClick={handleOpenSideBar}>â˜°</NavItem>
            <NavItem>
              <Link to={`/mmain`} style={{ color: '#333', textDecoration: 'none' }}>âŒ‚</Link>
            </NavItem>
            <NavItem><Link to={`/mmypage`} style={{ color: '#333', textDecoration: 'none' }}>ðŸ‘¤</Link></NavItem>
          </BottomNav>
        </PageContainer>
      );
  };

  export default MobilePostList;
