import React from 'react';
import styled from 'styled-components';
import logoImage from '../assets/Biglogo.png'; 
import { useNavigate,Link } from 'react-router-dom';

const SideBarContainer = styled.div`
  display: flex;
  flex-direction: column;
  min-height: 100vh;
  background-color: #fff6c3;
`;

const ContentArea = styled.div`
  flex: 1;
  padding: 20px;
  overflow-y: auto;
`;

const MenuList = styled.ul`
  list-style-type: none;
  padding: 0;
  margin: 0;
`;

const MenuItem = styled.li`
  margin-bottom: 20px;
  font-size: 18px;
  color: #333;
`;

const CloseButtonArea = styled.div`
  padding: 20px;
  background-color: #fff6c3;
`;

const CloseButton = styled.button`
  width: 100%;
  padding: 10px 20px;
  background-color: #333;
  color: white;
  border: none;
  border-radius: 5px;
  font-size: 16px;
  cursor: pointer;
`;

const LogoImage = styled.img`
  width: 300px;
  margin-top: 70px;
  margin-bottom: 30px;
`;

const SideBarPage = () => {
  const navigate = useNavigate();

  const handleClose = () => {
    navigate(-1); // 이전 페이지로 돌아가기
  };

  return (
    <SideBarContainer>
      <LogoImage src={logoImage} alt="Through World" />
      <ContentArea>
        <MenuList>
          <MenuItem><Link to="/mcommunity">국가별 커뮤니티</Link></MenuItem>
          <MenuItem><Link to="/mhots">HOT 게시글</Link></MenuItem>
          <MenuItem><Link to="/marrive">출국 전 정보</Link></MenuItem>
          <MenuItem><Link to="/mabroadlife">해외 생활 정보</Link></MenuItem>
          <MenuItem><Link to="/mback">귀국 전 정보</Link></MenuItem>
          <MenuItem><Link to="/">공동 구매</Link></MenuItem>
        </MenuList>
      </ContentArea>
      <CloseButtonArea>
        <CloseButton onClick={handleClose}>닫기</CloseButton>
      </CloseButtonArea>
    </SideBarContainer>
  );
};

export default SideBarPage;
